mapper.select:
SELECT a.id, a.type, a.`status`, a.title, a.nick, a.origin, a.create_at createAt, a.comments, a.likes, a.views, a.audit_at AS auditAt, a.audit_user_name AS auditUserName, a.reason FROM t_share_community AS a WHERE 1 = 1
AND a.title LIKE CONCAT('',#{searchFiled},'%') OR a.content LIKE CONCAT('%',#{searchFiled},'%')
AND a.type = #{type}
AND a.create_at >= DATE_FORMAT(#{startDate},'%Y-%m-%d %H:%i') AND a.create_at <= DATE_FORMAT(#{endDate},'%Y-%m-%d %H:%i')
AND a.status = #{status}
ORDER BY a.create_at DESC

SELECT a.id, a.type, a.`status`, a.title, a.nick, a.origin, a.create_at createAt, a.likes, a.views FROM t_share_community AS a WHERE 1 = 1
AND a.status = #{status}
ORDER BY a.create_at DESC
