mapper.select:
SELECT a.id, a.avatar, a.mobile, a.nick, a.sex, a.use_stauts AS useStauts, a.create_at AS createAt, a.province_name AS provinceName, a.city_name AS cityName, a.area_name AS areaName FROM t_user AS a
LEFT JOIN t_authentication AS b ON a.id = b.user_id
WHERE 1 = 1
AND ( a.`mobile` like CONCAT('%',#{searchFiled},'%') OR a.nick like CONCAT('%',#{searchFiled},'%') )
AND a.`create_at` BETWEEN #{startDate} AND #{endDate}
AND b.`status` = #{status}
AND a.`use_stauts` = #{useStauts}
AND a.`unit_nature` = #{unitNature}
ORDER BY a.`create_at` DESC
